    // let create_time = Math.round(new Date('2021-10-15 00:00:00').getTime() / 1000); //åœ¨æ­¤è¡Œä¿®æ”¹å»ºç«™æ—¶é—´
    // æœ‰è‹¹æžœç”¨æˆ·å‘çŽ°safariæµè§ˆå™¨ä¸èƒ½æ­£å¸¸è¿è¡Œï¼Œç™¾åº¦äº†ä¸€ä¸‹å‘çŽ°æ˜¯æ ¼å¼åŒ–çš„é—®é¢˜ï¼Œæ”¹æˆä¸‹é¢è¿™ç§åº”è¯¥å°±å¯ä»¥äº†ã€‚æ„Ÿè°¢åé¦ˆã€‚
    let create_time = Math.round(new Date('2025/4/16 08:00:00').getTime() / 1000); //åœ¨æ­¤è¡Œä¿®æ”¹å»ºç«™æ—¶é—´
    let timestamp = Math.round((new Date().getTime()) / 1000);
    let second = timestamp - create_time;
    let time = new Array(0, 0, 0, 0, 0);

    var nol = function(h) {
        return h > 9 ? h : '0' + h;
    }
    if (second >= 365 * 24 * 3600) {
        time[0] = parseInt(second / (365 * 24 * 3600));
        second %= 365 * 24 * 3600;
    }
    if (second >= 24 * 3600) {
        time[1] = parseInt(second / (24 * 3600));
        second %= 24 * 3600;
    }
    if (second >= 3600) {
        time[2] = nol(parseInt(second / 3600));
        second %= 3600;
    }
    if (second >= 60) {
        time[3] = nol(parseInt(second / 60));
        second %= 60;
    }
    if (second >= 0) {
        time[4] = nol(second);
    }
    let currentTimeHtml = ""
    if (time[0] != 0) {
        currentTimeHtml += time[0] + ' YEAR '
    }
    currentTimeHtml += time[1] + ' DAYS ' + time[2] + ' : ' + time[3] + ' : ' + time[4];
    document.getElementById("runtime").innerHTML = currentTimeHtml;
}, 1000);
